import{_ as e}from"./plugin-vue_export-helper-c27b6911.js";import{o as i,c as p,f as t}from"./app-e8fc33d4.js";const c={},n=t('<h1 id="acquiring-credentials-level-74" tabindex="-1"><a class="header-anchor" href="#acquiring-credentials-level-74" aria-hidden="true">#</a> Acquiring Credentials (level 74)</h1><p><strong>获取凭证</strong><br> 1.到<code>[-256 57 -4983]</code>领取护照</p><p>2.被拒绝后，和门外的<em>Doan</em>对话</p><p>3.跟着<em>Doan</em>沿着路来到酒吧<code>[-257 78 -7938]</code>，和里面的酒保对话</p><p>4.沿着路来到餐厅<code>[-210 84 -4942]</code>，在厨房里拿到三明治和水然后给外面的顾客上菜。</p><p>5.在货区拿到资源给对应的机长</p><div class="hint-container tip"><p class="hint-container-title">提示</p><p>灰色旗帜飞艇的机长需要小麦</p><p>绿色旗帜飞艇的机长需要南瓜</p><p>红色旗帜飞艇的机长需要石英</p></div><p>6.继续沿着路走，和梯子上的<em>Mechanic</em><code>[-261 101 -4999]</code>对话</p><p>7.跟着Mechanic抢夺飞艇</p><div class="hint-container tip"><p class="hint-container-title">怎么击倒守卫？</p><p>你只需要攻击他们就可以推下去了</p><p>顺便技能也起效，可以远程打下去</p></div><p>8.跟着Mechanic下去，在他偷蛋的时候保护他，然后离开</p><p>9.反击敌方飞艇，在敌方的炮弹接近时击打炮弹把它打回去。</p><p>10.回到酒吧，和酒保对话，进入酒保开启的隐藏通道</p><p>11.进入黑市，走到最里面和黑市成员<code>[-97 154 -6046]</code>对话，想办法凑齐2000颗绿宝石</p><div class="hint-container tip"><p class="hint-container-title">关于抬价</p><p>按这个数字找NPC：<br> 50-200-300-200(和先前的同一个)-150-150</p><p>原理：每一个NPC的底价永远是1000绿宝石</p><p>你可以找同一个NPC抬价两次，但是不能连续抬价</p><p>升价300绿宝石的NPC只能找一次</p><p>最多找5次NPC抬价</p></div><p>12.和黑市成员对话后，在门外触发剧情</p><p>奖励</p><ul><li>300000 经验值</li><li>768 绿宝石</li><li>1 Gavelian Passport(Quest Item)</li><li>可以使用Letvus电梯了(在机场)</li></ul>',18),a=[n];function l(o,r){return i(),p("div",null,a)}const _=e(c,[["render",l],["__file","level 74 - Aquiring of Credentials.html.vue"]]);export{_ as default};
